function valueToPercentage(value, min, max) {
    const clamped = clamp(value, min, max);
    return (max - min === 0) ? 0 : ((clamped - min) / (max - min)) * 100;
}
function valueToGaugeAngle(value, min, max) {
    const percentage = valueToPercentage(value, min, max) / 100;
    return -135 + (percentage * 270); // Rango de -135 a +135 grados
}

// --- CALCULAR ESTILOS DIN√ÅMICOS ---
// Fan animation duration (faster RPM = shorter duration)
// Map RPM (0-3000) to Duration (e.g., 20s - 0.2s)
let fanDuration = 20; // Default: very slow (20 seconds per rotation) if RPM is 0
if (Math.abs(data.rpm) > 10) { // Add a small threshold
    // Inverse relationship: higher RPM -> lower duration
    fanDuration = 20 / (Math.abs(data.rpm) / 50 + 1); // Adjust the divisor '50' to scale speed
    fanDuration = Math.max(0.2, fanDuration); // Ensure minimum duration (max speed)
}
const fanAnimationStyle = `spin ${fanDuration.toFixed(1)}s linear infinite ${data.rpm < 0 ? 'reverse' : 'normal'}`; // Add reverse for negative RPM

// --- CREAR OBJETO PAYLOAD ---
msg.payload = {
    // Valores num√©ricos
    tempValue: data.temp.toFixed(1),
    rpmValue: Math.round(data.rpm),
    torqueValue: data.torque.toFixed(1),
    currentValue: data.current.toFixed(1),
    voltageValue: data.voltage.toFixed(1),
    pwmValue: data.pwm.toFixed(0),
    loadValue: data.load.toFixed(1),

    // Estilos calculados
    tempFillHeight: valueToPercentage(data.temp, RANGES.temp.min, RANGES.temp.max) + '%',
    speedNeedleTransform: `rotate(${valueToGaugeAngle(data.rpm, RANGES.speed.min, RANGES.speed.max)}deg)`,
    torqueNeedleTransform: `rotate(${valueToGaugeAngle(data.torque, RANGES.torque.min, RANGES.torque.max)}deg)`,
    currentBarWidth: valueToPercentage(data.current, RANGES.current.min, RANGES.current.max) + '%',
    voltageBarWidth: valueToPercentage(data.voltage, RANGES.voltage.min, RANGES.voltage.max) + '%',

    // Estilo de animaci√≥n del ventilador
    fanAnimationStyle: fanAnimationStyle
};

return msg;

// --- RANGOS DE OPERACI√ìN (AJ√öSTALOS A TU SIMULACI√ìN) ---
const RANGES = {
    efficiency: { min: 0, max: 100 },  // %
    power: { min: 0, max: 120 },      // Watts (Ej: 12V * 10A, ajusta tu m√°ximo)
    error: { min: 0, max: 500 },       // RPM (Error RMS m√°ximo esperado)
