    rpm: msg.omega_rpm || RANGES.speed.min,     // A√±adido para gauge RPM
    torque: msg.torque_motor || RANGES.torque.min, // A√±adido para gauge Torque
    current: msg.corriente || RANGES.current.min, // A√±adido para gauge Corriente
    voltage: msg.V_motor || RANGES.voltage.min,   // A√±adido para gauge Voltaje
    pwm: msg.pwm || 0,                        // A√±adido para display
    load: msg.carga || 0                      // A√±adido para display
};

