function clamp(value, min, max) { return Math.max(min, Math.min(max, value)); }
function valueToPercentage(value, min, max) {
    const clamped = clamp(value, min, max);
    return (max - min === 0) ? 0 : ((clamped - min) / (max - min)) * 100;
}
function valueToGaugeAngle(value, min, max) { // Para gauge semicircular
    const percentage = valueToPercentage(value, min, max) / 100;
    return -90 + (percentage * 180); // Rango de -90 a +90 grados
}
function valueToDuration(value, valMin, valMax, durationMax, durationMin) { // Para animaciones
    if (value <= valMin) return durationMax;
    const rangeVal = valMax - valMin;
    const ratio = rangeVal === 0 ? 0 : Math.min(1, (value - valMin) / rangeVal);
    let duration = durationMax - ratio * (durationMax - durationMin);
    return Math.max(durationMin, duration);
}

