const rocketSpeedDuration = valueToDuration(data.power, 1, RANGES.power.max, 5, 0.3);
const rocketAnimationStyle = data.power > 1 ? `launch ${rocketSpeedDuration.toFixed(1)}s linear infinite` : 'none';

// 3. Energ√≠a/Potencia (Foco palpitante)
